import React, { useState } from 'react';
import axios from 'axios';

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const sendMessage = async () => {
    if (!input.trim()) return;

    setMessages([...messages, { text: input, sender: 'user' }]);
    setInput('');

    try {
      const response = await axios.post('/api/chat', { message: input });
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: response.data.message, sender: 'bot' }
      ]);
    } catch (error) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: 'Sorry, something went wrong. Please try again.', sender: 'bot' }
      ]);
    }
  };

  const fetchResponse = async (message) => {
    try {
      const response = await axios.post('https://api.chatbot-service.com/message', { message });
      setMessages([...messages, { sender: 'bot', text: response.data.reply }]);
    } catch (error) {
      console.error("Error fetching chatbot response:", error);
      setMessages([...messages, { sender: 'bot', text: "I'm sorry, I couldn't process your request. Please try again later." }]);
    }
  };

  return (
    <div className="chatbot">
      <div className="chat-window">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask me anything..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
